const datefns = global.get('datefns');
const datefnstz = global.get('datefnstz')
const timeZone = env.get("TZ");
const pvForecastFactor = env.get('pvForecastFactor');

const hourlyPvForecasts = {};
const result = (msg.payload.forecasts ?? []).forEach(item => {
    const periodStartLocalTime = global.get('toZonedDate')(new Date(item.period_end));
    const today = new Date();
    if (datefns.isSameDay(periodStartLocalTime, today)) {
        const key = datefnstz.format(periodStartLocalTime, 'yyyy-MM-dd\'T\'HH', { timeZone });
        if (hourlyPvForecasts[key]) {
            hourlyPvForecasts[key] = ((hourlyPvForecasts[key] + item.pv_estimate) / 2) * (pvForecastFactor ?? 1.0);
        } else {
            hourlyPvForecasts[key] = item.pv_estimate;
        }
    }
});

var newMsg = { topic: 'hourlyPvForecasts', payload: hourlyPvForecasts };
return newMsg;

