    if (costEfficientHoursDatePart === templateDatePart) {
        node.debug('costEfficientHours is up-to-date - fallback hours not needed');
    } else {
        node.debug('costEfficientHours is NOT up-to-date');
        if (fallbackHours) {
            node.debug('using fallback hours');
            const hourlyCost = new Map();
            const hourlyCostSelected = new Map();
            fallbackHours.forEach(entry => {
                if (Array.isArray(entry)) {
                    if (entry.length === 2) {
                        const dayOrRange = entry[0];
                        const hourOrRange = entry[1];
                        if (global.get('rangeContainsValue')(dow, dayOrRange) || dow === Number(dayOrRange)) {
                            global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                        }
                    } else if (entry.length === 1) {
                        const hourOrRange = entry[0];
                        global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                    }
                } else {
                    const hourOrRange = entry;
                    global.get('addSelectedHours')(template, hourOrRange, hourlyCostSelected, hourlyCost);
                }
            });
            return { payload: [...hourlyCostSelected.keys()] };
        }
    }
}

