global.set('buildKey', function (template, hour) {
    return template.substring(0, 11) + (Number(hour) < 10 ? `0${hour}` : hour);
});

global.set('getRangeBoundaries', function (range) {
    return String(range).split('-').map(boundary => Number(boundary));
});

global.set('rangeContainsValue', function (value, range) {
    const boundaries = String(range).split('-');
    if (boundaries.length == 2) {
        return Number(boundaries[0]) <= value && value <= Number(boundaries[1]);
    }
    return false;
});

global.set('addSelectedHours', function (template, hourOrRange, hourlyCostSelected, hourlyCost) {
    const boundaries = global.get('getRangeBoundaries')(hourOrRange);
    if (boundaries.length == 2) {
        for (let hour = boundaries[0]; hour <= boundaries[1]; hour++) {
            const key = global.get('buildKey')(template, hour)
            hourlyCostSelected.set(key, hourlyCost.get(key) ?? 0);
        }
    } else {
        const key = global.get('buildKey')(template, boundaries[0]);
        hourlyCostSelected.set(key, hourlyCost.get(key) ?? 0);
    }
});

