var datefns = global.get('datefns');

const costEfficientHoursYesterday = flow.get('costEfficientHoursYesterday');
node.debug(`OLD: costEfficientHoursYesterday=${costEfficientHoursYesterday}`);
const costEfficientHours = flow.get('costEfficientHours');
node.debug(`OLD: costEfficientHours=${costEfficientHours}`);
if (costEfficientHoursYesterday.length > 0) {
    const datePartCostEfficientHoursYesterdayFirstItem = costEfficientHoursYesterday[0].substring(0, 10);
    node.debug(`datePartCostEfficientHoursYesterdayFirstItem=${datePartCostEfficientHoursYesterdayFirstItem}`);

    const dateMidnightCostEfficientHoursYesterday = datefns.parse(datePartCostEfficientHoursYesterdayFirstItem, "yyyy-MM-dd", global.get('toZonedDate')(new Date()));
    node.debug(`dateMidnightCostEfficientHoursYesterday=${dateMidnightCostEfficientHoursYesterday}`);

    let todayMidnight = global.get('toZonedDate')(new Date());
    todayMidnight = datefns.setHours(todayMidnight, 0);
    todayMidnight = datefns.setMinutes(todayMidnight, 0);
    todayMidnight = datefns.setSeconds(todayMidnight, 0);
    node.debug(`todayMidnight=${todayMidnight}`);

    const differenceInDays = datefns.differenceInDays(todayMidnight, dateMidnightCostEfficientHoursYesterday);
    node.debug(`differenceInDays=${differenceInDays}`);

    if (differenceInDays !== 1) {
        node.debug(`NEW: costEfficientHoursYesterday=${costEfficientHours}`);
        flow.set('costEfficientHoursYesterday', costEfficientHours);
    }
} else {
    node.debug(`NEW: costEfficientHoursYesterday=${costEfficientHours}`);
    flow.set('costEfficientHoursYesterday', costEfficientHours);
}
